///* XPM */
// #-> white
// .-> grey
// ' '-> BALCK
// + -> YELLOW
// @-> BROWN?/
void print_beer_icon (int x, int y){
int col;
for(int dx=0; dx<150; dx++){
	for(int dy=0;dy<150;dy++){
		switch(beer_five[dx][dy]) {

		   case ' '  :
		   col=BLACK;
		   break;
		   case '#'  :
		   col=WHITE;
		   break;
		   case '.'  :
		   col=GRAY;
		   break;
		   case '+'  :
		   col= YELLOW;
		   break;
		   case '@' :
		   col= MAROON;
		   break;
		   default:
		   col= WHITE;
		   break;
		}
		WriteAPixel(x+dx, y+dy, col);		
	}
}
}

